    // Pre-increment
    a = 5;  // Reset
    printf("++a returns: %d\n", ++a);
    printf("After ++a, a = %d\n\n", a);

    // Post-decrement
    a = 5;  // Reset
    printf("a-- returns: %d\n", a--);
    printf("After a--, a = %d\n\n", a);

    // Pre-decrement
    a = 5;  // Reset
    printf("--a returns: %d\n", --a);
    printf("After --a, a = %d\n\n", a);

    // Practical: Loop counter
    printf("Countdown: ");
    for (int i = 5; i > 0; i--) {
