#include <stdio.h>
#include <string.h>
#include <ctype.h>

int main() {
    char str[100] = "Hello World";
    char original[100];
    strcpy(original, str);

    printf("Original: %s\n\n", original);

    // 1. Reverse string
    int len = strlen(str);
    for (int i = 0, j = len - 1; i < j; i++, j--) {
        char temp = str[i];
        str[i] = str[j];
        str[j] = temp;
    }
    printf("Reversed: %s\n", str);

    // 2. Convert to uppercase
    strcpy(str, original);
    for (int i = 0; str[i] != '\0'; i++) {
        str[i] = toupper(str[i]);
    }
    printf("Uppercase: %s\n", str);

    // 3. Convert to lowercase
    for (int i = 0; str[i] != '\0'; i++) {
        str[i] = tolower(str[i]);
    }
    printf("Lowercase: %s\n", str);

    // 4. Replace character
    strcpy(str, original);
    for (int i = 0; str[i] != '\0'; i++) {
        if (str[i] == 'o') {
            str[i] = '*';
        }
    }
    printf("Replace 'o' with '*': %s\n", str);

    return 0;
}

