// Check if palindrome
int isPalindrome(char str[]) {
    int len = strlen(str);
    for (int i = 0; i < len/2; i++) {
        if (str[i] != str[len-1-i])
            return 0;  // Not palindrome
    }
    return 1;  // Is palindrome
}

