#include <stdio.h>

int main() {
    int choice = 2;
    int score = 85;

    // === SWITCH: Good for discrete values ===
    printf("=== Switch Example ===\n");
    printf("Menu choice: %d\n", choice);

    switch (choice) {
        case 1:
            printf("Action: New Game\n");
            break;
        case 2:
            printf("Action: Load Game\n");
            break;
        case 3:
            printf("Action: Settings\n");
            break;
        default:
            printf("Invalid choice\n");
    }

    // === IF-ELSE: Good for ranges ===
    printf("\n=== If-Else Example ===\n");
    printf("Score: %d\n", score);

    if (score >= 90) {
        printf("Grade: A (Excellent)\n");
    } else if (score >= 80) {
        printf("Grade: B (Very Good)\n");
    } else if (score >= 70) {
        printf("Grade: C (Good)\n");
    } else if (score >= 60) {
        printf("Grade: D (Pass)\n");
    } else {
        printf("Grade: F (Fail)\n");
    }

    // === COMPARISON ===
    printf("\n=== When to Use What ===\n");

    // This works but if-else is better (range checking)
    int age = 25;
    printf("Age: %d\n", age);

    // Awkward with switch (would need case for every age)
    // Better with if-else:
    if (age < 13) {
        printf("Category: Child\n");
    } else if (age < 20) {
        printf("Category: Teen\n");
    } else if (age < 60) {
        printf("Category: Adult\n");
    } else {
        printf("Category: Senior\n");
    }

    // Perfect for switch (discrete values)
    char grade = 'B';
    printf("\nGrade: %c\n", grade);

    switch (grade) {
        case 'A':
            printf("Excellent!\n");
            break;
        case 'B':
            printf("Very Good!\n");
            break;
        case 'C':
            printf("Good!\n");
            break;
        default:
            printf("Keep trying!\n");
    }

    return 0;
}

