    printf("\n=== Simple Calculator Menu ===\n");
    printf("1. Add\n");
    printf("2. Subtract\n");
    printf("3. Multiply\n");
    printf("4. Divide\n");
    printf("0. Exit\n");
    printf("Choice: ");
    scanf("%d", &choice);

    float a, b, result;

    switch (choice) {
        case 1:
            printf("Enter two numbers: ");
            scanf("%f %f", &a, &b);
            result = a + b;
            printf("Result: %.2f + %.2f = %.2f\n", a, b, result);
            displayMenu();  // Show menu again
            break;

        case 2:
            printf("Enter two numbers: ");
            scanf("%f %f", &a, &b);
            result = a - b;
            printf("Result: %.2f - %.2f = %.2f\n", a, b, result);
            displayMenu();  // Show menu again
            break;

        case 3:
            printf("Enter two numbers: ");
            scanf("%f %f", &a, &b);
            result = a * b;
            printf("Result: %.2f * %.2f = %.2f\n", a, b, result);
            displayMenu();  // Show menu again
            break;

        case 4:
            printf("Enter two numbers: ");
            scanf("%f %f", &a, &b);
            if (b != 0) {
                result = a / b;
                printf("Result: %.2f / %.2f = %.2f\n", a, b, result);
            } else {
                printf("Error: Division by zero\n");
            }
            displayMenu();  // Show menu again
            break;

        case 0:
            printf("\nThank you! Goodbye.\n");
            // Base case - don't call menu again
            break;

        default:
            printf("Invalid choice! Try again.\n");
            displayMenu();  // Show menu again
    }
}

// State machine example
void processState(int state) {
    printf("\nCurrent State: %d\n", state);

    switch (state) {
        case 1:
            printf("State 1: Initialization\n");
            printf("Moving to State 2...\n");
            processState(2);
            break;

        case 2:
            printf("State 2: Processing\n");
            printf("Moving to State 3...\n");
            processState(3);
            break;

        case 3:
            printf("State 3: Validation\n");
            printf("Moving to State 4...\n");
            processState(4);
            break;

        case 4:
            printf("State 4: Completion\n");
            printf("Process finished!\n");
            // Base case - stop
            break;

        default:
            printf("Invalid state!\n");
    }
}

int main() {
