    }

    return 0;
}

#include <stdio.h>

// Linear search
int linearSearch(int arr[], int size, int target) {
    for (int i = 0; i < size; i++) {
        if (arr[i] == target) {
            return i;  // Found at index i
        }
    }
    return -1;  // Not found
}

// Find minimum
int findMin(int arr[], int size) {
    int min = arr[0];
    for (int i = 1; i < size; i++) {
        if (arr[i] < min) {
            min = arr[i];
        }
    }
    return min;
}

// Reverse array
void reverseArray(int arr[], int size) {
    for (int i = 0; i < size / 2; i++) {
        int temp = arr[i];
        arr[i] = arr[size - 1 - i];
        arr[size - 1 - i] = temp;
    }
}

// Bubble sort
void bubbleSort(int arr[], int size) {
    for (int i = 0; i < size - 1; i++) {
        for (int j = 0; j < size - i - 1; j++) {
            if (arr[j] > arr[j + 1]) {
                // Swap
                int temp = arr[j];
                arr[j] = arr[j + 1];
                arr[j + 1] = temp;
            }
        }
    }
}

void printArray(int arr[], int size) {
    for (int i = 0; i < size; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");
}

int main() {
    int numbers[] = {64, 34, 25, 12, 22, 11, 90};
    int size = 7;

    printf("=== Original Array ===\n");
    printArray(numbers, size);

    // Linear search
    printf("\n=== Linear Search ===\n");
    int target = 22;
    int index = linearSearch(numbers, size, target);
    if (index != -1) {
        printf("Found %d at index %d\n", target, index);
    } else {
        printf("%d not found\n", target);
    }

    // Find minimum
    printf("\n=== Find Minimum ===\n");
    printf("Minimum value: %d\n", findMin(numbers, size));

    // Reverse array
    printf("\n=== Reverse Array ===\n");
    reverseArray(numbers, size);
    printf("Reversed: ");
    printArray(numbers, size);

    // Restore and sort
    int numbers2[] = {64, 34, 25, 12, 22, 11, 90};
    printf("\n=== Bubble Sort ===\n");
    printf("Before: ");
    printArray(numbers2, size);
    bubbleSort(numbers2, size);
    printf("After:  ");
    printArray(numbers2, size);

    return 0;
}

